mapper.select:
SELECT

FROM sys_code a
WHERE a.id = #{id}

SELECT a.id
FROM sys_code a
WHERE a.del_flag = #{DEL_FLAG_NORMAL} and a.value = #{value}

	AND a.parent_id = #{parent.id}



	AND a.parent_id is null



	AND a.id != #{id}

ORDER BY a.sort

SELECT a.id
FROM sys_code a
WHERE a.del_flag = #{DEL_FLAG_NORMAL} and a.comCode = #{comCode}

	AND a.parent_id = #{parent.id}



	AND a.parent_id is null



	AND a.id != #{id}

ORDER BY a.sort

mapper.update:
UPDATE sys_code SET 
	parent_id = #{parent.id}, 
	parent_ids = #{parentIds}
WHERE id = #{id}
